document.addEventListener("page:loaded",()=>{const{appid:t,appkey:n}=CONFIG.changyan;const a="https://cy-cdn.kuaizhan.com/upload/changyan.js";const e=`https://cy-cdn.kuaizhan.com/upload/plugins/plugins.list.count.js?clientId=${t}`;setTimeout(()=>{return NexT.utils.getScript(e,{attributes:{async:true,id:"cy_cmt_num"}})},0);if(CONFIG.page.comments&&!CONFIG.page.isHome){NexT.utils.loadComments("#SOHUCS").then(()=>{return NexT.utils.getScript(a,{attributes:{async:true}})}).then(()=>{window.changyan.api.config({appid:t,conf:n})}).catch(t=>{console.error("Failed to load Changyan",t)})}});