document.addEventListener("DOMContentLoaded",()=>{const{indexName:e,appID:t,apiKey:a,hits:s}=CONFIG.algolia;const i=instantsearch({indexName:e,searchClient:algoliasearch(t,a),searchFunction:e=>{if(document.querySelector(".search-input").value){e.search()}}});if(typeof pjax==="object"){i.on("render",()=>{pjax.refresh(document.querySelector(".algolia-hits"))})}i.addWidgets([instantsearch.widgets.configure({hitsPerPage:s.per_page||10}),instantsearch.widgets.searchBox({container:".search-input-container",placeholder:CONFIG.i18n.placeholder,showReset:false,showSubmit:false,showLoadingIndicator:false,cssClasses:{input:"search-input"}}),instantsearch.widgets.stats({container:".algolia-stats",templates:{text:e=>{const t=CONFIG.i18n.hits_time.replace("${hits}",e.nbHits).replace("${time}",e.processingTimeMS);return`<span>${t}</span>
            <img src="${CONFIG.images}/logo-algolia-nebula-blue-full.svg" alt="Algolia">`}},cssClasses:{text:"search-stats"}}),instantsearch.widgets.hits({container:".algolia-hits",escapeHTML:false,templates:{item:e=>{const{title:t,excerpt:a,excerptStrip:s,contentStripTruncate:i}=e._highlightResult;let n=`<a href="${e.permalink}" class="search-result-title">${t.value}</a>`;const l=a||s||i;if(l&&l.value){const r=document.createElement("div");r.innerHTML=l.value;n+=`<a href="${e.permalink}"><p class="search-result">${r.textContent.substring(0,100)}...</p></a>`}return n},empty:e=>{return`<div class="algolia-hits-empty">
              ${CONFIG.i18n.empty.replace("${query}",e.query)}
            </div>`}},cssClasses:{list:"search-result-list"}}),instantsearch.widgets.pagination({container:".algolia-pagination",scrollTo:false,showFirst:false,showLast:false,templates:{first:'<i class="fa fa-angle-double-left"></i>',last:'<i class="fa fa-angle-double-right"></i>',previous:'<i class="fa fa-angle-left"></i>',next:'<i class="fa fa-angle-right"></i>'},cssClasses:{list:["pagination","algolia-pagination"],item:"pagination-item",link:"page-number",selectedItem:"current",disabledItem:"disabled-item"}})]);i.start();document.querySelectorAll(".popup-trigger").forEach(e=>{e.addEventListener("click",()=>{document.body.classList.add("search-active");setTimeout(()=>document.querySelector(".search-input").focus(),500)})});const n=()=>{document.body.classList.remove("search-active")};document.querySelector(".search-pop-overlay").addEventListener("click",e=>{if(e.target===document.querySelector(".search-pop-overlay")){n()}});document.querySelector(".popup-btn-close").addEventListener("click",n);document.addEventListener("pjax:success",n);window.addEventListener("keyup",e=>{if(e.key==="Escape"){n()}})});