document.addEventListener("DOMContentLoaded",()=>{if(!CONFIG.path){console.warn("`hexo-generator-searchdb` plugin is not installed!");return}const n=new LocalSearch({path:CONFIG.path,top_n_per_article:CONFIG.localsearch.top_n_per_article,unescape:CONFIG.localsearch.unescape});const o=document.querySelector(".search-input");const t=()=>{if(!n.isfetched)return;const e=o.value.trim().toLowerCase();const t=e.split(/[-\s]+/);const s=document.querySelector(".search-result-container");let c=[];if(e.length>0){c=n.getResultItems(t)}if(t.length===1&&t[0]===""){s.classList.add("no-result");s.innerHTML='<div class="search-result-icon"><i class="fa fa-search fa-5x"></i></div>'}else if(c.length===0){s.classList.add("no-result");s.innerHTML='<div class="search-result-icon"><i class="far fa-frown fa-5x"></i></div>'}else{c.sort((e,t)=>{if(e.includedCount!==t.includedCount){return t.includedCount-e.includedCount}else if(e.hitCount!==t.hitCount){return t.hitCount-e.hitCount}return t.id-e.id});const r=CONFIG.i18n.hits.replace("${hits}",c.length);s.classList.remove("no-result");s.innerHTML=`<div class="search-stats">${r}</div>
        <hr>
        <ul class="search-result-list">${c.map(e=>e.item).join("")}</ul>`;if(typeof pjax==="object")pjax.refresh(s)}};n.highlightSearchWords(document.querySelector(".post-body"));if(CONFIG.localsearch.preload){n.fetchData()}if(CONFIG.localsearch.trigger==="auto"){o.addEventListener("input",t)}else{document.querySelector(".search-icon").addEventListener("click",t);o.addEventListener("keypress",e=>{if(e.key==="Enter"){t()}})}window.addEventListener("search:loaded",t);document.querySelectorAll(".popup-trigger").forEach(e=>{e.addEventListener("click",()=>{document.body.classList.add("search-active");setTimeout(()=>o.focus(),500);if(!n.isfetched)n.fetchData()})});const s=()=>{document.body.classList.remove("search-active")};document.querySelector(".search-pop-overlay").addEventListener("click",e=>{if(e.target===document.querySelector(".search-pop-overlay")){s()}});document.querySelector(".popup-btn-close").addEventListener("click",s);document.addEventListener("pjax:success",()=>{n.highlightSearchWords(document.querySelector(".post-body"));s()});window.addEventListener("keyup",e=>{if(e.key==="Escape"){s()}})});